$(document).ready(function() {
    // Get the input field and member cards
    var searchInput = $("#searchInput");
    var memberCards = $(".member-card");

    // Listen for input changes on the search field
    searchInput.on("input", function() {
        // Get the search query
        var searchQuery = searchInput.val().toLowerCase();

        // Show/hide member cards based on search query
        memberCards.each(function() {
            var memberName = $(this).find("h5").text().toLowerCase();
            var memberSpecialist = $(this).find(".text-sm").text().toLowerCase();
            if (memberName.includes(searchQuery) || memberSpecialist.includes(searchQuery)) {
                $(this).show();
            } else {
                $(this).hide();
            }
        });
    });
});
